import logo from './logo.svg';
import './App.css';
import contract from './contracts/ExampleNFT.json';
import {useEffect, useState} from "react";

const contractAddress = "0xA9e018881796Bf2bb2721346807a0e3fb82D99f8"
const abi =contract.abi;

function App() {

  const [currentAccount, setCurrentAccount] = useState(null);

  const checkWalletIsConnected = async () => {
    const { ethereum } = window;
    if(!ethereum) {
      console.log(("메타마스크가 설치되어 있는지 확인하세요."));
      return;
    } else {
      console.log("지갑이 확인되었습니다.")
    }

    const accounts = await ethereum.request({method: 'eth_accounts'});
    if(accounts.left !== 0){
      const account = accounts[0];
      console.log("인증된 계좌 : ", account);
      setCurrentAccount(account);
    }
  }

  const connectWalletHandler = async () => {
    const {ethereum} = window;
    if (!ethereum) {
      alert("메타마스크를 설치하세요.")
    }

    try {
      const accounts = await ethereum.request({method: 'eth_requestAccounts'});
      console.log("계좌주소 : ", accounts[0])
    } catch (err) {
      console.log(err)
    }
  }

  const mintNftHandler = async () => {
    try {
      const { ethereum } = window;

      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const nftContract = new ethers.Contract(contractAddress, abi, signer);

        console.log("Initialize payment");
        let nftTxn = await nftContract.mintNFTs(1, { value: ethers.utils.parseEther("0.01") });

        console.log("Mining... please wait");
        await nftTxn.wait();

        console.log(`Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTxn.hash}`);

      } else {
        console.log("Ethereum object does not exist");
      }

    } catch (err) {
      console.log(err);
    }
  }

  const connectWalletButton = () => {
    return (
        <button onClick={connectWalletHandler} className='cta-button connect-wallet-button'>
          Connect Wallet
        </button>
    )
  }

  const mintNftButton = () => {
    return (
        <button onClick={mintNftHandler} className='cta-button mint-nft-button'>
          Mint NFT
        </button>
    )
  }

  useEffect(() => {
    checkWalletIsConnected();
  }, [])

  return (
      <div className='main-app'>
        <h1>Scrappy Squirrels Tutorial</h1>
        <div>
          {connectWalletButton()}
        </div>
      </div>
  )
}

export default App;
